import { Fragment } from "react";
import { useHistory, useLocation } from "react-router-dom";

import QuoteItem from "./QuoteItem";
import classes from "./QuoteList.module.css";

const QuoteList = (props) => {
  const history = useHistory();
  const location = useLocation();

  // converting loacation data to a object which is better for us
  // then we can extract query paprams by key
  const queryParams = new URLSearchParams(location.search); //? this is default javascript
  console.log(queryParams);

  const isSortingAscending = queryParams.get("sort") === "asc";

  const changeSortingHandler = () => {
    history.push("/quotes?sort=asc");
  };

  return (
    <Fragment>
      <div className={classes.sorting}>
        <button onClick={changeSortingHandler}>
          Sort {isSortingAscending}
        </button>
      </div>
      <ul className={classes.list}>
        {props.quotes.map((quote) => (
          <QuoteItem
            key={quote.id}
            id={quote.id}
            author={quote.author}
            text={quote.text}
          />
        ))}
      </ul>
    </Fragment>
  );
};

export default QuoteList;
